import React, {Component} from 'react';

class Formulario extends Component {

    // Refs son para leer los valores de los campos de un formulario

    constructor(props){
        super(props);
        this.marcaRef = React.createRef();
        this.yearRef = React.createRef();
        this.planBasicoRef = React.createRef();
        this.planCompletoRef = React.createRef();
    }

    //podemos omitir el constructor y solo poner:

    /*
        marcaRef = React.createRef();
        yearRef = React.createRef();
        planBasicoRef = React.createRef();
        planCompletoRef = React.createRef();

    */

    //Nota: En lugar de usar handleCotizarSeguro = (e) => {
        //se puede dejar handleCotizarSeguro(e){
        //y usar this.handleCotizarSeguro.bind(this)


    handleCotizarSeguro = (e) => {
        e.preventDefault(); //Previene el comportamiento por default

        //leer el plan
        const plan = this.planBasicoRef.current.checked ? 'basico' : 'completo';

        //obtener los datos

        //console.log(this.marcaRef.current.value);

        //crear el objetto

        const infoAuto = {
            marca: this.marcaRef.current.value,
            year: this.yearRef.current.value,
            plan : plan
        }

        //console.log(infoAuto);

        //enviar al componente principal con props

        this.props.cotizarSegro(infoAuto);

        //resetear el formulario

        e.currentTarget.reset();
    }


    render() {
        return (
            <form className="cotizar-auto" onSubmit={this.handleCotizarSeguro}>
                <div className="campo">
                    <label>Marca</label>
                    <select name="marca" ref={this.marcaRef}>
                        <option value="americano">Americano</option>
                        <option value="europeo">Europeo</option>
                        <option value="asiatico">Asiatico</option>
                    </select>
                </div>

                <div className="campo">
                    <label>Año</label>
                    <select name="year" ref={this.yearRef}>
                        <option value="2018">2018</option>
                        <option value="2017">2017</option>
                        <option value="2016">2016</option>
                        <option value="2015">2015</option>
                        <option value="2014">2014</option>
                        <option value="2013">2013</option>
                        <option value="2012">2012</option>
                        <option value="2011">2011</option>
                        <option value="2010">2010</option>
                        <option value="2009">2009</option>
                        <option value="2008">2008</option>
                    </select>
                </div>
                <div className="campo">
                    <label>Plan:</label>
                    <input type="radio" ref={this.planBasicoRef} name="plan" value="basico"/> Básico
                    <input type="radio" ref={this.planCompletoRef} name="plan" value="completo"/> Completo
                </div>

                <button type="submit" className="boton">Cotizar</button>
            </form>
        );
    }
}

export default Formulario;
