import React, { Component } from 'react';

//Componentes
import Header from './Header';
import Formulario from './Formulario';

//Helpers -- nos ayudan a no tener todo revuelto

import {obtenerDiferenciaAnio, calcularMarca} from '../helper';

class App extends Component {

  cotizarSeguro = (datos) => {
    const {marca, plan, year} = datos;

    //Agregar una base de 2000
    let resultado = 2000;

    //Obtener la diferencia de a単os y
    const diferencia = obtenerDiferenciaAnio(year);

    //por cada a単o restar el 3% al valor del seguro
    resultado -= ((diferencia * 3) * resultado) / 100;

    //Americano 15% Asiatico 5% y Europeo 30% de 
    //incremento al valor actual

    resultado = calcularMarca(marca) * resultado;

    console.log(resultado);

  }

  render() {
    return (
      <div className="contenedor">
       <Header
         titulo='Cotizador de seguro de Auto'
         />
        <div className="contenedor-formulario">
          <Formulario
           cotizarSeguro = {this.cotizarSeguro}  //Este prop llegara a fomulario y de regreso recibira los datos
          />
        </div>
      </div>

    );
  }
}

export default App;
